import React, { useState } from 'react'
import {Avatar , Button , Paper , Grid , Typography , Container, TextField} from '@material-ui/core';
import useStyles from './styles'
import LockOutlinedIcon from '@material-ui/icons/LockOpenOutlined'
import Input from './Input'

const Auth = () => {
    const [showPassword, setShowPassword] = useState(false)
    const classes = useStyles();

    const isSignup = false;
    const handleShowPassword = ()=>setShowPassword((prevShowPassword)=> !prevShowPassword)

    const handleSubmit = ()=>{

    }
    const handleChange = ()=>{

    }

    return (
        <Container component = 'main' maxWidth ='xs'>
            <Paper classes = {classes.paper} elevation = {3}>
                <Avatar className = {classes.avatar}>
                    <LockOutlinedIcon/>
                </Avatar>
                <Typography variant = 'h5'>{isSignup ? 'Sign Up' : 'Login'}</Typography>
                <form className = {classes.form} onSubmit = {handleSubmit}>
                    <Grid container spacing = {2} >
                        {
                            isSignup && (
                                <>
                                    <Input name = 'firstName' lable = 'First Name' handleChange = {handleChange} autoFocus half/>
                                    <Input name = 'firstName' lable = 'First Name' handleChange = {handleChange}  half/>  
                                </>
                            )
                        }
                        <Input name = 'email' label = "Email Adress" handleChange= {handleChange} type = 'email'/>
                        <Input name = 'password' label = "Password" handleChange = {handleChange} type = {showPassword ? 'text' : 'password'} handleShowPassword = {handleShowPassword}/>
                        { isSignup && <Input name = 'confirmPassword' lable = "Repeat Password" handleChange= {handleChange} type = 'password'/>} 
                    </Grid>
                    <Button type = 'submit' fullWidth variant = 'contained' color ='primary' className = {classes.submit}  >
                        {isSignup ? 'Sign Up ' : 'Login'}
                    </Button>
                </form>
            </Paper>
        </Container>
    )
}

export default Auth
